  const showHandler=()=>{
    setShow((prev)=>!prev)
  }
  return (
    <div className="app">
      <h1>Hi there!</h1>
      {show && <p>This is new</p>}

      {/* <Button onClick={showHandler} > Click me</Button> */}
    </div>
  );
}

export default App;
